mapper.insert:
INSERT INTO book
    ( bookid
    , title
    , author
    , price
    , isbn
    , publish)
VALUES 
    ( #{bookId}
     ,#{title}
     ,#{author}
     ,#{price}
     ,#{isbn}
     ,#{publish} )

mapper.update:
UPDATE book b     
   SET b.title = #{title}
     , b.author = #{author}
     , b.PRICE    = #{price}
     , b.isbn = #{isbn}
     , b.publish = #{publish}
     , b.MODDATE  = sysdate
 WHERE b.bookid = #{bookId}

mapper.select:
SELECT b.bookid   
  FROM book b    
 WHERE b.bookid = #{bookId}
